mapper.update:
update ordercr
set orderstatus = 4

    and orderid = #{orderid}
    and orderstatus = 1
    and starttime  <=  #{starttime}

mapper.select:
SELECT oc.orderid, oc.cid, oc.starttime, oc.endtime, oc.theme, oc.snum, oc.stelephone, oc.faculty, oc.groupname, oc.teacher, oc.ttelephone, oc.attendcount, oc.orderstatus, oc.createtime, oc.details, s.sname from ordercr oc inner join student s on oc.snum = s.snum where oc.snum = #{snum}
and oc.cid = #{cid}
and oc.starttime like concat('%',#{startdate},'%')
order by starttime desc limit #{offset},#{limit}

mapper.update:
update ordercr
set orderstatus = 3

    and snum = #{snum}
    and createtime  <=  #{createtime}
    and orderstatus = 0

mapper.select:
select count(*)
from ordercr

    snum = #{snum}

        and cid = #{cid}


        and starttime like concat('%',#{startdate},'%')

SELECT * from classroom
and cid = #{cid}
and starttime like concat('%',#{startdate},'%')

mapper.update:
update ordercr
set cid = #{cid},starttime = #{starttime},endtime = #{endtime},snum = #{snum},stelephone = #{stelephone},faculty = #{faculty},groupname = #{groupname},teacher = #{teacher},ttelephone = #{ttelephone},orderstatus = 0, details = #{details}
where orderid = #{orderid}

update ordercr
set orderstatus = #{orderstatus}
where
    orderid = #{orderid}
    and orderstatus = 0
